(function($){let startTime=(new Date).getTime();let actions=[];function getTimeSpent(){let endTime=(new Date).getTime();return Math.floor((endTime-startTime)/1e3)}function getVisitDate(){let visitDate=new Date;let year=visitDate.getFullYear();let month=String(visitDate.getMonth()+1).padStart(2,"0");let day=String(visitDate.getDate()).padStart(2,"0");let hours=String(visitDate.getHours()).padStart(2,"0");let minutes=String(visitDate.getMinutes()).padStart(2,"0");let seconds=String(visitDate.getSeconds()).padStart(2,"0");return`${year}-${month}-${day} ${hours}:${minutes}:${seconds}`}function getLanguage(){const userLanguage=navigator.language||navigator.userLanguage;return userLanguage}function getActions(){$(document).on("click",function(event){const elementId=event.target.id||"";const elementClass=event.target.className||"";actions.push({event:event.type,element:event.target.tagName,x:event.pageX,y:event.pageY,id:elementId,"class":elementClass,timestamp:(new Date).getTime()})})}function sendAnalyticsData(){const data={action:"simple_analytics_track_visit",nonce:simpleAnalytics.nonce,page_url:window.location.href,language:getLanguage(),time_spent:getTimeSpent(),visit_date:getVisitDate(),actions:JSON.stringify(actions)};$.post(simpleAnalytics.ajax_url,data,function(response){if(response.success){console.log("Dati di analytics inviati con successo.")}else{console.log("Errore nell'invio dei dati di analytics.")}})}getActions();window.addEventListener("beforeunload",sendAnalyticsData)})(jQuery);